import { AfterViewInit, Component, DoCheck, OnInit, ViewChild } from '@angular/core';
import { HeaderComponent } from '../header/header.component';
import { Room, RoomList } from './rooms';

@Component({
  selector: 'hinv-rooms',
  templateUrl: './rooms.component.html',
  styleUrls: ['./rooms.component.scss']
})
export class RoomsComponent implements OnInit, DoCheck, AfterViewInit {

  tableTitle = 'Rooms List';
  hotelName = 'Hilton Hotel at Atlanta';
  numberOfRooms = 10;
  hideRooms = false;
  selectedRoom !: RoomList;

  rooms: Room = {
    totalRooms: 20,
    availableRooms: 10,
    bookedRooms: 5
  };

  roomList: RoomList[] = [];

  @ViewChild(HeaderComponent) headerComponent!: HeaderComponent;;

  constructor() { }

  ngOnInit(): void {
    // console.log(this.headerComponent); static = true if you want to implement it inside oninit().

    this.roomList = [
      {
        roomNumber: 1,
        roomType: 'Delexue Room',
        ammenities: 'AIR CONDITIONER, WIFI, TV',
        price: 5000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('11-Nov-2023'),
        checkOutTime: new Date('12-Nov-2023'),
        rating: 3.1
      },
      {
        roomNumber: 2,
        roomType: 'Superior Room',
        ammenities: 'AIR CONDITIONER, No WIFI, TV',
        price: 3000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('12-Nov-2023'),
        checkOutTime: new Date('15-Nov-2023'),
        rating: 3.6

      },
      {
        roomNumber: 3,
        roomType: 'Normal Room',
        ammenities: 'AIR CONDITIONER',
        price: 1000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('15-Nov-2023'),
        checkOutTime: new Date('19-Nov-2023'),
        rating: 4.7
      },
      {
        roomNumber: 4,
        roomType: 'Living Room',
        ammenities: 'AIR CONDITIONER',
        price: 1000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('15-Nov-2023'),
        checkOutTime: new Date('19-Nov-2023'),
        rating: 4.9
      },
      {
        roomNumber: 5,
        roomType: 'Suite Room',
        ammenities: 'AIR CONDITIONER',
        price: 1000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('15-Nov-2023'),
        checkOutTime: new Date('19-Nov-2023'),
        rating: 1.5
      },
      {
        roomNumber: 6,
        roomType: 'Full Room',
        ammenities: 'AIR CONDITIONER',
        price: 1000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('15-Nov-2023'),
        checkOutTime: new Date('19-Nov-2023'),
        rating: 2.5
      },
      {
        roomNumber: 7,
        roomType: 'Queen Room',
        ammenities: 'AIR CONDITIONER',
        price: 1000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('15-Nov-2023'),
        checkOutTime: new Date('19-Nov-2023'),
        rating: 3.5
      },
      {
        roomNumber: 8,
        roomType: 'King Room',
        ammenities: 'AIR CONDITIONER',
        price: 1000,
        photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
        checkInTime: new Date('15-Nov-2023'),
        checkOutTime: new Date('19-Nov-2023'),
        rating: 4.2
      }
    ]
  }
  ngDoCheck(): void {
    console.log('On changes is called');
  }

  toggle() {
    this.hideRooms = !this.hideRooms;
    this.tableTitle = "Toggle Room List";
  }

  selectRoom(room: RoomList) {
    this.selectedRoom = room;
  }

  addRoom() {
    const room : RoomList = {
      roomNumber: 9,
      roomType: 'Queen Room',
      ammenities: 'AIR CONDITIONER, WIFI',
      price: 1500,
      photos: 'https://images.pexels.com/photos/1571458/pexels-photo-1571458.jpeg?cs=srgb&dl=pexels-vecislavas-popa-1571458.jpg&fm=jpg',
      checkInTime: new Date('15-Nov-2023'),
      checkOutTime: new Date('19-Nov-2023'),
      rating: 4.2
    };
    // this.roomList.push(room);
    this.roomList = [...this.roomList, room]; // Use of spread operator to retain the previous records and add the new records to the room list
  }

  ngAfterViewInit(): void {
    // console.log(this.headerComponent);
    this.headerComponent.header = 'Header from Header Component';
  }

}
<!-- <h1> Welcome to {{ hotelName }} </h1> -->
<hinv-header

></hinv-header>

<div [hidden] = "hideRooms">
<div [ngStyle] = "{'color': rooms.totalRooms ? 'green' : 'red', 'display': 'inline-block'}">
    Number of Total Rooms: <span [innerText]="rooms.totalRooms"></span>
</div>
</div>

<!-- {{roomList | json}} -->

<br>

<div *ngIf="selectedRoom">
    {{selectedRoom | prettyJson}}
</div>

<span [ngStyle] = "{'color': rooms.totalRooms ? 'blue' : 'red'}">Number of Available Rooms: {{ rooms.availableRooms ?? 'No Rooms Available' }}</span>
<div *ngIf="rooms && rooms.availableRooms !== undefined && rooms.availableRooms > 0">
    <br>
   <hinv-rooms-list [rooms]="roomList"
   [tableTitle]="tableTitle"
   (selectedRoom)="selectRoom($event)"
   ></hinv-rooms-list>
  </div>
  <br>

<div>
<button (click)="addRoom()">Add Room</button>
</div>
<br>

<button (click) = "toggle()">Toggle</button>
